import { useParams, Link } from 'react-router-dom';
import './CourseDetails.css';

const courseInfo = {
  bca: {
    name: "Bachelor of Computer Applications (BCA)",
    seats: 120,
    fees: "₹35,000 per semester",
    duration: "3 years (6 semesters)",
    startDate: "August 1, 2025",
    endDate: "July 31, 2028",
    syllabus: "/syllabus/bca.pdf",
    description: "BCA is an undergraduate course focusing on computer science, programming, and software development."
  },
  mca: {
    name: "Master of Computer Applications (MCA)",
    seats: 60,
    fees: "₹45,000 per semester",
    duration: "2 years (4 semesters)",
    startDate: "August 1, 2025",
    endDate: "July 31, 2027",
    syllabus: "/syllabus/mca.pdf",
    description: "MCA is a postgraduate program focused on advanced computer applications, software engineering, and data systems."
  }
  // Add more courses as needed
};

function CourseDetails() {
  const { id } = useParams();
  const course = courseInfo[id];

  return (
    <div className="course-details">
      {course ? (
        <>
          <h1>{course.name}</h1>
          <p><strong>Seats:</strong> {course.seats}</p>
          <p><strong>Fees:</strong> {course.fees}</p>
          <p><strong>Duration:</strong> {course.duration}</p>
          <p><strong>Start Date:</strong> {course.startDate}</p>
          <p><strong>End Date:</strong> {course.endDate}</p>
          <p><strong>Description:</strong> {course.description}</p>
          <p><strong>Syllabus:</strong> <a href={course.syllabus} target="_blank" rel="noreferrer">Download PDF</a></p>
          <Link to="/courses" className="back-link">← Back to Courses</Link>
        </>
      ) : (
        <p>No information found for this course.</p>
      )}
    </div>
  );
}

export default CourseDetails;
