import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import '../styles/AdminDashboard.css';

function AdminDashboard() {
  const navigate = useNavigate();
  const [admissions, setAdmissions] = useState([]);
  const [grievances, setGrievances] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // ‚¨ÖÔ∏è ADD THIS

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/auth");
      return;
    }

    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/auth/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const fetchedUser = res.data.user;
        setUser(fetchedUser);

        if (fetchedUser.role !== "admin") {
          alert("Access denied. Admins only.");
          navigate("/a");
        } else {
          fetchForms(); // only fetch forms after role check
        }
      } catch (err) {
        console.error("User fetch error:", err);
        navigate("/auth");
      } finally {
        setLoading(false); // ‚¨ÖÔ∏è stop loading
      }
    };

    const fetchForms = async () => {
      try {
        const admissionRes = await axios.get("http://localhost:5000/api/admissions");
        const grievanceRes = await axios.get("http://localhost:5000/api/grievances");
        setAdmissions(admissionRes.data);
        setGrievances(grievanceRes.data);
      } catch (error) {
        console.error("Error fetching form data:", error);
      }
    };

    fetchUser();
  }, [navigate]);

  if (loading) return <div>Loading admin dashboard...</div>; // ‚¨ÖÔ∏è Prevent early redirect/render

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      {user && <h2>Welcome, {user.name}</h2>}

      <div className="form-section">
        <h3>üìã Admission Submissions</h3>
        <ul>
          {admissions.map((admission, idx) => (
            <li key={idx}>
              <strong>{admission.fullName}</strong> - {admission.email}
            </li>
          ))}
        </ul>
      </div>

      <div className="form-section">
        <h3>üì® Grievance Submissions</h3>
        <ul>
          {grievances.map((grievance, idx) => (
            <li key={idx}>
              <strong>{grievance.name}</strong> - {grievance.email}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default AdminDashboard;
