import './Home.css';
import About from './About';
import { useEffect, useState } from 'react';

const sliderImages = [
  "/campus.jpg",
  "/building.jpg",
  "/auditoriam.jpg",
  "/lab1.jpg",
  "/lab2.jpg",
  "/lab3.jpg",
  "/classroom.jpg", 
  "/library.jpg",
  "/parking.jpg",
  "/ground.jpg",

];

function Home() {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === sliderImages.length - 1 ? 0 : prevIndex + 1
      );
    }, 1000); 

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home">
      <section
        className="hero"
        style={{
          backgroundImage: `url(${sliderImages[currentIndex]})`
        }}
      >
        <div className="hero-content">
          <h1>College Portal</h1>
          <p>Your gateway to academic resources, faculty, and campus life.</p>
          <a href="/courses" className="hero-btn">Explore More</a>
        </div>
      </section>

      <section className="highlights">
        <div className="highlight">
          <h3>üìö 30+ Courses</h3>
          <p>Diverse UG & PG programs across various departments.</p>
        </div>
        <div className="highlight">
          <h3>üë®‚Äçüè´ Expert Faculty</h3>
          <p>Learn from experienced professors and researchers.</p>
        </div>
        <div className="highlight">
          <h3>üèõÔ∏è Campus Facilities</h3>
          <p>Modern labs, digital library, sports & more.</p>
        </div>
      </section>

      <About />
    </div>
  );
}

export default Home;
